operations.schema:
input RemoveDeliverableInput {
  id: OID!
}

input SetDeliverableProgressInput {
  id: OID! #deliverable id
  workProgress: ProgressInput
}

input ProgressInput {
  # Only one of these fields should be provided
  percentage: Float
  storyPoints: StoryPointInput
  done: Boolean
}

input StoryPointInput {
  total: Int!
  completed: Int!
}

input AddKeyResultInput {
  id: OID!
  deliverableId:OID!
  title: String!
  link: String
}

input RemoveKeyResultInput {
  id: OID!
  deliverableId:OID!
}

input EditKeyResultInput {
  id: OID!
  deliverableId: OID!
  title: String
  link: URL
}

input AddRoadmapInput {
  id: OID!
  title: String!
  slug: String
  description: String
}

input RemoveRoadmapInput {
  id: OID!
}

input RemoveMilestoneInput {
  id: OID!
  roadmapId:OID!
}

input AddCoordinatorInput {
  id: ID! 
  milestoneId: OID!
}

input RemoveCoordinatorInput {
  id: ID! 
  milestoneId: OID! # Coordinator can potentially be coordinating multiple milestones/roadmaps. 
}

input AddMilestoneDeliverableInput {
  milestoneId: OID!
  deliverableId: OID!
  title: String!
}

input RemoveMilestoneDeliverableInput {
  milestoneId: OID!
  deliverableId: OID!
}

input EditDeliverablesSetInput {
  milestoneId: ID
  projectId: ID
  status: DeliverableSetStatusInput
  deliverablesCompleted: DeliverablesCompletedInput
}

enum DeliverableSetStatusInput {
  DRAFT
  TODO
  IN_PROGRESS
  FINISHED
  CANCELED
}

input DeliverablesCompletedInput {
  total: Int!
  completed: Int!
}

input AddDeliverableInSetInput {
  milestoneId: ID
  projectId: ID
  deliverableId: OID!
}

input RemoveDeliverableInSetInput {
  milestoneId: ID
  projectId: ID
  deliverableId: OID!
}

operations.reducer:
// Check if agent with same ID already exists
const existingAgent = state.contributors.find(agent => agent.id === action.input.id);
if (existingAgent) {
  throw new AgentDuplicateIdError(`Agent with ID ${action.input.id} already exists`);
}

// Create new agent with correct structure matching GraphQL schema
const agent = {
  id: action.input.id,
  name: action.input.name,
  icon: action.input.icon || null,
  description: action.input.description || null,
};

state.contributors.push(agent);

operations.schema:
input AddAgentInput {
  id: PHID!
  name: String!
  icon: URL
  description: String
}

operations.reducer:
// Find agent by ID
const agentIndex = state.contributors.findIndex(agent => agent.id === action.input.id);
if (agentIndex === -1) {
  throw new AgentNotFoundError(`Agent with ID ${action.input.id} not found`);
}

// Remove agent from contributors array
state.contributors.splice(agentIndex, 1);

operations.schema:
input RemoveAgentInput {
  id: PHID!
}

operations.reducer:
// Find agent by ID
const agentIndex = state.contributors.findIndex(agent => agent.id === action.input.id);
if (agentIndex === -1) {
  throw new AgentNotFoundError(`Agent with ID ${action.input.id} not found`);
}

// Update agent with provided fields, preserving existing values for optional fields
const existingAgent = state.contributors[agentIndex];
const updatedAgent = {
  ...existingAgent,
  name: action.input.name !== undefined ? action.input.name : existingAgent.name,
  icon: action.input.icon !== undefined ? action.input.icon : existingAgent.icon,
  description: action.input.description !== undefined ? action.input.description : existingAgent.description,
};

state.contributors[agentIndex] = updatedAgent;

